
// Other file types

declare module "*.scss" {
    const content: any;
    export default content;
}

declare module "*.png" {
    const content: string;
    export default content;
}

declare module "*.svg" {
    const content: string;
    export default content;
}

declare module "*.jpg" {
    const content: string;
    export default content;
}

declare module "*.gif" {
    const content: string;
    export default content;
}